import styled from 'styled-components';

export const Container= styled.div `
 position:relative ;
 left:5%;
 width:90%;
 height: 600px;
 background-color: #333;
`;
export const Cardprof = styled.div`
  width:250px;
  position:relative;
  left:10%;
  top:15%;
  height: 100px;
  background-size:cover;
  background-repeat: no-repeat;
  background-position: center;
  border-top-left-radius: 9px;
  border-top-right-radius: 9px;


`;

export const CardInfo = styled.div`
 position: relative;
 top:100px;
 background-color:#f1f1f1;
 width:250px;
 height: 260px;
 border-bottom-left-radius: 9px;
 border-bottom-right-radius: 9px;
 

 img{
     position: relative;
     bottom: 60px;
     left: 30%;
     width:100px;
     height: 100px;
     border-radius: 50%;
     
 }
 p{
     position: relative;
     bottom:25%;
     font-family: 'Hina Mincho';
     text-align: center;
     color:rgb(129, 128, 154);
 }
 h2{
    position: relative;
     bottom:30%;
     font-family: 'Hina Mincho';
     text-align: center;
 }
 h4{
    position: relative;
     bottom:38%;
     font-size: 16px;
     font-family: 'Hina Mincho';
     text-align: center;
 }
`;

export const DescAtv = styled.div`
 position:relative;
 top:90px;
 left:35%;
 padding: 10px;
 
`;
