import styled from "styled-components";

export const boxText = styled.div`
 position: relative;
 text-align: center;
 color:white;
 font-size: 20px;
 padding:30px;
 text-align: center;
 width:300px;
 border-radius: 3px;
 border-bottom: 1px solid rgb(4, 0, 240);   
 height:350px;
 background:linear-gradient(315deg, #dad7d7 20%, #121224 74%);
 box-shadow: 0px 3px 15px rgb(12, 12, 12);
`;
