import styled from 'styled-components';

export const Container = styled.div`
position: relative;
left: 6px;
 width: 45%;
 background-color: #f1f1f1;

 h4{
     text-align: center;
     font-size: 20px;
     font-family: 'Hina Mincho';
 }
 img{
     width:300px;
 }
`;
export const ImgCard = styled.div`
 width:600px;
`;
