import React from 'react';
import './estilo/modalp.css';
import atv44 from '../../img/FOTOS PAGINA/atv44.jpeg';

export default function Modalp({closeM}) {
 return (
   <div className='background'>
       <div className='container'>
           <button onClick={()=> closeM(false)}>X</button>
           <div className='left'>
           <img src={atv44}/>
           </div>
           <div className='rigth'>
               <h1>Acompanhamento Pedagogico</h1>
               <p>No Projeto Visão Social são oferecidas aulas de reforço escolar com o companhamento da pedagoga e da educadora social as turmas são separadas de acordo com a faixa etaria e as atividades são desenvolvidas de acordo com a necessidade de cada criança e adolescente atendida no Projeto. Todas as atividades trabalhadas são equiparadas. A metodologia empregada é socio constrututivista onde a criança e o adolescente são estimilu  .</p>
           </div>

       </div> 
   </div>
 );
}
